#!/usr/bin/python
import subprocess
import sys
import shlex

printerOptions = { "MachinePhase":"Phase3", "PaperSources":"PC408", "Finisher":"FS529", "PrinterHDD":"True",  }

cmd = ['/usr/bin/lpoptions', '-p', 'Versailles', '-l']
proc = subprocess.Popen(cmd, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
(lpoptLongOut, lpoptErr) = proc.communicate()

# lpoptions -p printername -l will still exit 0 even if printername does not exist
# but it will print to stderr
if lpoptErr:
    print lpoptErr
    sys.exit(0)

cmd = ['/usr/bin/lpoptions', '-p', 'Versailles']
proc = subprocess.Popen(cmd, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
(lpoptOut, lpoptErr) = proc.communicate()

#Note: lpoptions -p printername will never fail. If Versailles does not exist, it
#will still exit 0, but just produce no output.
#Thanks, cups, I was having a good day until now.

for option in lpoptLongOut.splitlines():
    for myOption in printerOptions.keys():
        optionName = option.split("/", 1)[0]
        optionValues = option.split("/",1)[1].split(":")[1].strip().split(" ")
        for opt in optionValues:
            if "*" in opt:
                actualOptionValue = opt.replace('*', '')
                break
        if optionName == myOption:
            if not printerOptions[myOption].lower() == actualOptionValue.lower():
                print "Found mismatch: %s is '%s', should be '%s'" % (myOption, printerOptions[myOption], actualOptionValue)
                sys.exit(0)

optionDict = dict()
for builtOption in shlex.split(lpoptOut):
    optionDict[builtOption.split("=")[0]] = builtOption.split("=")[1]

comparisonDict = { "device-uri":"lpd://Versailles.mit.edu", "printer-info":"Main Street (Color)", "printer-location":"Main Street Print Area (E19-259 )", "printer-make-and-model":"KONICA MINOLTA C280 PS" }
for keyName in comparisonDict.keys():
    if not comparisonDict[keyName] == optionDict[keyName]:
        print "Settings mismatch: %s is '%s', should be '%s'" % (keyName, optionDict[keyName], comparisonDict[keyName])
        sys.exit(0)

sys.exit(1)

#!/usr/bin/python import subprocess import sys
# Populate these options if you want to set specific options for the printer. E.g. duplexing installed, etc. printerOptions = { "MachinePhase":"Phase3", "PaperSources":"PC408", "Finisher":"FS529", "PrinterHDD":"True", }
cmd = [ '/usr/sbin/lpadmin', '-x', 'Versailles' ] proc = subprocess.Popen(cmd, stdout=subprocess.PIPE, stderr=subprocess.PIPE) (lpadminxOut, lpadminxErr) = proc.communicate()
# Install the printer cmd = [ '/usr/sbin/lpadmin', '-p', 'Versailles', '-L', 'Main Street Print Area (E19-259 )', '-D', 'Main Street (Color)', '-v', 'lpd://Versailles.mit.edu', '-P', "/Library/Printers/PPDs/Contents/Resources/KONICAMINOLTAC280.gz", '-E', '-o', 'printer-is-shared=false', '-o', 'printer-error-policy=abort-job' ]
for option in printerOptions.keys(): cmd.append("-o") cmd.append(str(option) + "=" + str(printerOptions[option]))
proc = subprocess.Popen(cmd, stdout=subprocess.PIPE, stderr=subprocess.PIPE) (lpadminOut, lpadminErr) = proc.communicate()
if lpadminErr: print "Error: %s" % lpadminErr sys.exit(1) print "Results: %s" % lpadminOut sys.exit(0)
